import React, { Component } from 'react';
import './App.css';
import FlipMove from "react-flip-move";


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      items: [],
      currentIteam: {
        text: '',
        key: ''
      }
    }

    this.handleInput = this.handleInput.bind(this)
    this.AddIteam = this.AddIteam.bind(this)
    this.DeleteItems = this.DeleteItems.bind(this)
    this.setUpdate = this.setUpdate.bind(this)
  }

  handleInput(e) {
    this.setState({
      currentIteam: {
        text: e.target.value,
        key: Date.now()
      }
    })
  }

  AddIteam(e) {
    e.preventDefault();
    const newiteam = this.state.currentIteam;
    console.log(newiteam);
    if (newiteam !== "") {
      const newIteams = [...this.state.items, newiteam];
      this.setState({
        items: newIteams,
        currentIteam: {
          text: '',
          key: ''
        }
      })
    }
  }

  DeleteItems(key) {
    const fliterItems = this.state.items.filter(two => two.key !== key);
    console.log(fliterItems);
    this.setState({ items: fliterItems })
  }

  setUpdate(text, key) {
    const items = this.state.items;
    items.map(item => {
      if (item.key === key) {
        item.text = text;
      }
    });

    this.setState({
      items: items
    })
  }

  render() {
    return (
      <div className="first-todo">
        <div className="second-input">
          <header >
          <nav class="navbar navbar-inverse">
  <div class="container-fluid">
    <div class="navbar-header">
      <button type="button" class="navbar-toggle" data-toggle="collapse" data-target="#myNavbar">
        <span class="icon-bar"></span>
        <span class="icon-bar"></span>
        <span class="icon-bar"></span>
      </button>
      <a class="navbar-brand" href="#">WebSiteName</a>
    </div>
    <div class="collapse navbar-collapse" id="myNavbar">
      <ul class="nav navbar-nav">
        <li class="active"><a href="#">Home</a></li>
        <li><a href="#">Page 1</a></li>
        <li><a href="#">Page 2</a></li>
        <li><a href="#">Page 3</a></li>
      </ul>
      <ul class="nav navbar-nav navbar-right">
        <li><a href="#"><span class="glyphicon glyphicon-user"></span> Sign Up</a></li>
        <li><a href="#"><span class="glyphicon glyphicon-log-in"></span> Login</a></li>
      </ul>
    </div>
  </div>
</nav>


                <div className="col-6" style={{textAlign:"center"}}>
            <div className="input-group">
              <div className="input-group-prepend">
                <span className="input-group-text" id="basic-addon">
                  <h2>✎</h2>
                </span>
              </div>
              <textarea className="form-control" id="exampleFormControlTextarea1" rows="5" cols="2" type="text" placeholder="Enter your text" value={this.state.currentIteam.text} onChange={this.handleInput}></textarea>
            </div>
            </div>

            <button type="button" class="btn btn-primary" variant="contained" color="secondary" onClick={this.AddIteam} style={{ height: 50, marginTop: 50 }}>upload your text</button>

          </header>
          <Listiteams items={this.state.items} DeleteItems={this.DeleteItems} setUpdate={this.setUpdate} />

        </div>

      </div>
    )
  }
}



function Listiteams(props) {
  const items = props.items;
  const listData = items.map(one => {
    return <div className="list-input" key={one.key}>
      <p style={{ boxShadow: "2px 2px 2px 2px darkgrey" }}>
        <textarea type="text"
          placeholder="here you can edit also"
          id={one.key} value={one.text}
          onChange={(e) => { props.setUpdate(e.target.value, one.key) }}
          style={{ fontSize: "20px", border: "0px" }} />
        <button type="button" class="btn btn-primary" fontSize="large" onClick={() => { props.DeleteItems(one.key) }} >submit</button>            </p>
    </div>
  })
  return (<div>
    <FlipMove duration={700} easing="ease-in-out">
      {listData}
    </FlipMove>
  </div>)
}

export default App;
