import React, { Component } from 'react';
import { useForm } from "react-hook-form";
import className from "classnames"

import './App.css';



function App() {
  const { register, handleSubmit, errors } = useForm({ });
  console.log(errors);

  const onSubmit = (data) => console.log(data);

  return (

    <div className="container py-5 ">
      <h1 style={{ textAlign: "center" }}>React Validate Form</h1>
      <form action="" onSubmit={handleSubmit(onSubmit)}>
        <div className="form-group">
          <label htmlFor="fullname">Full Name</label>
          <input
            type="text"
            className={className("form-control", { "is-invalid": errors.fullname })}
            id="fullname"
            placeholder="Enter Your Full Name"
            ref={register({
              required: "This field Your FullName",
              minLength: {
                value: 5,
                message: "entar the fullname"
              }
            })}
            name="fullname"
          />

          {errors.fullname && (<div className="invalid-feedback">{errors.fullname.message}</div>)}


        </div>
        <div className="form-group">
          <label htmlFor="email">E-mail Address</label>
          <input
            type="text"
            className={className("form-control", { "is-invalid": errors.email })}
            id="email"
            placeholder="This field Your E-mail Address"
            ref={register({
              required: "this filed is required",
              pattern: {
                value: /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/,
                message: "pleace entar the email"
              }
            })}

            name="email"
          />

          {errors.email && (<div className="invalid-feedback">{errors.email.message}</div>)}

        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone Number</label>
          <input
            type="text"
            className={className("form-control", { "is-invalid": errors.phone })}


            id="phone"
            placeholder="This field Your Phone Number"
            ref={register({
              required: "this filed is required",
              pattern: {
                value: /^\d{10}$/,
                message: " entar the 10 digit number"
              }
            })}

            name="phone"
          />
          {errors.phone && (<div className="invalid-feedback">{errors.phone.message}</div>)}

        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="text"
            className={className("form-control", { "is-invalid": errors.password })}

            id="password"
            placeholder="Enter Your Password"
            ref={register({
              required: "this filed is required",
              pattern: {
                value: /(?=^.{8,}$)((?=.*\d)|(?=.*\W+))(?![.\n])(?=.*[A-Z])(?=.*[a-z]).*$/,
                message: " (UpperCase, LowerCase, Number/SpecialChar and min 8 Chars)"
              }
            })}
            name="password"
          />
          {errors.password && (<div className="invalid-feedback">{errors.password.message}</div>)}

        </div>
        <div className="form-group">
          <label htmlFor="state">Choose Your State</label>
          <select className={className("form-control", { "is-invalid": errors.state })}
            id="state" ref={register({
              required: "this filed is required",


            })}
            name="state">
            <option value="">--- Select Your State ---</option>
            <option value="Himachal Pradesh">Himachal Pradesh</option>
            <option value="Gujarat">Gujarat</option>
            <option value="Bengaluru">Bengaluru</option>
            <option value="Pune">Pune</option>
          </select>
        </div>
        {errors.state && (<div className="invalid-feedback">{errors.state.message}</div>)}


        <button className="btn btn-primary" type="submit">Create New Account</button>
      </form>    </div>
  );
}

export default App;
