import React from 'react';
import './App.css';
import FlipMove from "react-flip-move";
import Button from '@material-ui/core/Button'; // @material-ui_Button
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField'

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));



class App extends React.Component {
  constructor() {
    super()
    this.state = {
      items: [],
      currentIteam: {
        text: '',
        key: ''
      }
    }

    this.handleInput = this.handleInput.bind(this)
    this.AddIteam = this.AddIteam.bind(this)
    this.DeleteItems = this.DeleteItems.bind(this)
    this.setUpdate = this.setUpdate.bind(this)
  }

  handleInput(e) {
    this.setState({
      currentIteam: {
        text: e.target.value,
        key: Date.now()
      }
    })
  }

  AddIteam(e) {
    e.preventDefault();
    const newiteam = this.state.currentIteam;
    console.log(newiteam);
    if (newiteam !== "") {
      const newIteams = [...this.state.items, newiteam];
      this.setState({
        items: newIteams,
        currentIteam: {
          text: '',
          key: ''
        }
      })
    }
  }

  DeleteItems(key) {
    const fliterItems = this.state.items.filter(two => two.key !== key);
    console.log(fliterItems);
    this.setState({ items: fliterItems })
  }

  setUpdate(text, key) {
    const items = this.state.items;
    items.map(item => {
      if (item.key === key) {
        item.text = text;
      }
    });

    this.setState({
      items: items
    })
  }

  render() {
  const classes = useStyles();

    return (

      <div className="first-todo">
        <div className="second-input">
          <header >
            <h1 style={{ textAlign: "center" }}>
              TODO LIST
          </h1>

                <TextareaAutosize aria-label="empty textarea" placeholder="Enter your text" value={this.state.currentIteam.text} onChange={this.handleInput} />;

                {/* <textarea className="form-control" id="exampleFormControlTextarea1" rows="5" cols="2" type="text" placeholder="Enter your text"></textarea> */}

            <Button variant="contained" color="primary" onClick={this.AddIteam}>
              Hello World
           </Button>

          </header>
          <Listiteams items={this.state.items} DeleteItems={this.DeleteItems} setUpdate={this.setUpdate} />

        </div>

      </div>
    )
  }
}



function Listiteams(props) {
  const items = props.items;
  const listData = items.map(one => {
    return <div key={one.key}>
      <p style={{ boxShadow: "2px" }}>
        <div className="container">
          <input type="text"
            class="form-control"
            placeholder="here you can edit also"
            id={one.key} value={one.text}
            onChange={(e) => { props.setUpdate(e.target.value, one.key) }}
            style={{ fontSize: "20px" }} />
        </div>

        <button style={{ margin: "-3% 2% 1.5% 86%" }} className="input-group-prepend" type="button" className="btn btn-primary" fontSize="large" onClick={() => { props.DeleteItems(one.key) }} >DELETE</button>
      </p>
    </div>
  })
  return (<div>
    <FlipMove duration={700} easing="ease-in-out">
      {listData}
    </FlipMove>
  </div>)
}

export default App;
